UPDATE tbDispatchTaskDetail set State=2,FinishTime='$FinishTime$' where dispatchNo='$dispatchNo$' and LandCode='$LandCode$' and DetailID=$TaskDetailID$;
UPDATE tbLocaton SET LockState = 0 ,LockCar = '' where LankMarkCode='$LandCode$';
UPDATE tbDispatchTaskDetail set IsCallGoods=2 where dispatchNo='$dispatchNo$' and LandCode='$LandCode$' and DetailID=$TaskDetailID$ and IsCallGoods=1;
UPDATE tbDispatchTaskInfo set FinishTime='$FinishTime$',TaskState=2 where not exists( select 1 from tbDispatchTaskDetail where dispatchNo='$dispatchNo$' and State in (0,1) ) and dispatchNo='$dispatchNo$';
delete from tbAGVCurrentRoute where AGVID=$AGVID$;

UPDATE tbDispatchTaskDetail set State=$State$,FinishTime='$FinishTime$' where dispatchNo='$dispatchNo$' and DetailID=$TaskDetailID$;
UPDATE tbDispatchTaskDetail set IsNeedCallBack=3 where $State$=2 and IsNeedCallBack=1 and dispatchNo='$dispatchNo$' and DetailID=$TaskDetailID$;
--如果是手工重做，需要将主任务中的执行车辆制成空,否则还会指派该车，因为 --重做希望让其他车来接着做这个任务 update tbDispatchTaskInfo set ExeAgvID=0,FinishTime='',TaskState=0 where dispatchNo='$dispatchNo$' and $State$=0;
update tbDispatchTaskDetail set State=0,FinishTime='' where dispatchNo='$dispatchNo$' and $State$=0;
UPDATE tbDispatchTaskDetail set IsNeedCallBack=1 where $State$=0 and IsNeedCallBack>0 and dispatchNo='$dispatchNo$' and DetailID=$TaskDetailID$;
update tbDispatchTaskInfo set TaskState=$State$,FinishTime='$FinishTime$' where 0=( select count(1) Amount from tbDispatchTaskDetail where dispatchNo='$dispatchNo$' and State!=$State$ ) and dispatchNo='$dispatchNo$'

      UPDATE  tbDispatchTaskInfo
